import React from "react";
import { NavLink } from "react-router-dom";
import { changeLocation } from "../../actions/changeLocation";
import { store } from "../..";

export class Navbar extends React.Component {
  render() {
    const changeConsumer = () => {
      const newLocation = window.location.pathname.substring(
        window.location.pathname.lastIndexOf("/") + 1
      );
      store.dispatch(changeLocation(newLocation));
    };

    return (
      <nav>
        <ul className="nav-list">
          <li>
            <NavLink
              to="/clothes/women"
              className={`${
                this.props.pathname === "/clothes/women" ? "active" : ""
              }`}
              onClick={changeConsumer}
            >
              Women
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/clothes/men"
              className={`${
                this.props.pathname === "/clothes/men" ? "active" : ""
              }`}
              onClick={changeConsumer}
            >
              Men
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/clothes/kids"
              className={`${
                this.props.pathname === "/clothes/kids" ? "active" : ""
              }`}
            >
              Kids
            </NavLink>
          </li>
        </ul>
      </nav>
    );
  }
}
