import React from "react";
import Wrapper from "../Wrapper";
import { connect } from "react-redux";
import { addCartItem } from "../../redux/actions/cart";
import ToggleButtons from "../product/ToggleButtons";
import { currenciesSymbols } from "../../constants";

class CartPage extends React.Component {

    setButtonClassName = (type, value, id) => {
        for (let val of value) {
            let attrs = Object.values(val);
            console.log(id)
            console.log(attrs.includes(id))

        }
        // console.log(id)
        // if (this.props.attributes[id] === value) {
        //     return type === "swatch" ? "attribute-color-active" : "attribute-text-active";
        // } else {
        //     return "attribute-button";
        // }
    }

    render() {
        return (
            <Wrapper>
                <p className="cart-title">cart</p>

                {
                    this.props.cartItems.map(item =>
                        <div className="cart-item-container">
                            <div key={item.id} className="product-info product-info-cart">
                                <h3>{item.brand}</h3>
                                <p>{item.name}</p>
                                <p key={Math.random()} className="price">
                                    {currenciesSymbols[this.props.selectedCurrency] || '$'} {item.prices.find(x => x.currency === this.props.selectedCurrency)?.amount}
                                </p>
                                {
                                    item.attributes.map(attribute => <div>
                                        {
                                            attribute.items.map(attr => <button
                                            className={this.setButtonClassName(attribute.type, attribute.items, item.attrValues)}
                                            style={{ ...attribute.type === "swatch" ? { backgroundColor: attr.value } : "" }}
                                            >{attribute.type === "swatch" ? "" : attr.value}</button>
                                            )}
                                    </div>)
                                }
                            </div>
                        </div>
                    )
                }
            </Wrapper>
        )
    }
}

const mapStateToProps = state => {
    return {
        selectedCurrency: state.currencies.selected,
        cartItems: state.cart.list,
        products: state.products.list,
    };
};

export default connect(
    mapStateToProps,
    { addCartItem }
)(CartPage);
