import React from "react";

export class Cart extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isCartOpen: false,
        }
    }

    toggleCart = () => {
        this.setState((prevState) => ({
            isCartOpen: !prevState.isCartOpen,
        }));
    };

    render() {

        const { isCartOpen } = this.state;

        return (
            <div className="cart-dd-wrapper">
                <img alt="" className="cart" src="../../empty-cart.svg" onClick={this.toggleCart}></img>
                {(isCartOpe) && <div role="list" className="dd-list">
                    {this.props.list.map((item) => (
                        <button
                            type="button"
                            className="dd-list-item"
                            key={item}
                            onClick={() => this.clickOnCurrency(item)}
                        >
                            {this.getSymbolForCurrency(item)} {item}
                        </button>
                    ))})
                    </div>
                    }

            </div>
        )
    }
}
