import React from "react";

export class ProductCard extends React.Component {
    render() {
        console.log(this.props.selectedCurrency)
        getSymbolForCurrency = currency => currenciesSymbols[currency] || '$';

        return (
            this.props.products
                .map(x => x
                    .map(product => product.category === this.props.selectedCategory
                        && <div className="card">
                            <img alt="" className="buy-icon" src="./buy-icon.svg"></img>
                            <img className="item-image" src={product.gallery[0]}></img>
                            <div className="item-info">
                                <p>{product.name}</p>
                                <p className="price">{this.getSymbolForCurrency(this.props.selectedCurrency)}50.00</p>
                            </div>
                        </div>))
        );
    }
}
