import React from "react";
import { currenciesSymbols } from "../../constants";

export class Cart extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        const setButtonClassName = (type, value, id) => {
            if (this.state[id] === value) {
                return type === "swatch" ? "attribute-color-active" : "attribute-text-active";
            } else {
                return "attribute-button";
            }
        }
        return (
            <div className="cart-dd-wrapper">
                <img alt="" className={this.props.isCartOpen ? "cart-open" : "cart"} src="../../empty-cart.svg" onClick={() => {
                    this.props.toggleCart();
                }}></img>
                {(this.props.isCartOpen) && <div role="list" className="cart-list">
                    <div className="cart-wrapper">
                        <p className="cart-title"><b>My bag</b>, 2 items</p>
                        {
                            this.props.cartItems.map(product => <div className="cart-popup-item">
                                <div className="cart-popup-info">
                                    <p>{product.brand}</p>
                                    <p>{product.name}</p>
                                    {product.prices.map(price => price.currency === this.props.selectedCurrency 
                                    && <p>{currenciesSymbols[this.props.selectedCurrency] || '$'}<b>{price.amount}</b></p>)}
                                    <div className="cart-popup-attributes">
                                    {product.attributes.map(attribute => attribute.items.map(item => <button
                                            className={setButtonClassName(attribute.type, item.value, attribute.id)}
                                            key={item.id}
                                            style={{ ...attribute.type === "swatch" ? { backgroundColor: item.value } : "" }}
                                        >
                                            {attribute.type === "swatch" ? "" : item.value}
                                        </button>
                                        ))}
                                    </div>
                                </div>
                            </div>
                            )
                        }
                        {/* <div className="cart-popup-item">
                            <div className="cart-popup-info">
                                <p>Apollo</p>
                                <p>Running short</p>
                                <p><b>$50.00</b></p>
                                <div className="cart-popup-attributes">
                                    <button className="cart-popup-button">
                                        S
                                    </button>
                                    <button className="cart-popup-button">
                                        M
                                    </button>
                                </div>
                            </div>
                            <div className="cart-popup-image">
                                <div className="cart-popup-amount">
                                    <button className="cart-popup-button">
                                    +
                                    </button>
                                    <p>1</p>
                                    <button className="cart-popup-button">
                                    -
                                    </button>
                                </div>
                                <img alt="" src="./img.png"></img>
                            </div>
                        </div>  */}
                        <p className="cart-popup-total">Total: <b>$50.00</b></p>
                        <div className="cart-buttons">
                            <button>view bag</button>
                            <button className="cart-popup-green-button">check out</button>
                        </div>
                    </div>
                </div>
                }
            </div>
        )
    }
}
