import React from "react";
import { currenciesSymbols } from '../constants';

export default class ProductPage extends React.Component {
    getProduct() {
        return this.props.products.map(x => x.map(product => 
            product.id === this.props.product && product.attributes));
    }

    render() {
        return (
            <main>
                <div className="wrapper">
                    <div className="side-images">
                        {
                            this.props.products
                                .map(x => x
                                    .map(product => product.id === this.props.product
                                        && product.gallery.map(x => <img key={x} alt="" className="side-image" src={x}></img>)))
                        }
                    </div>
                    <div className="product-wrapper">
                        {
                            this.props.products
                                .map(x => x
                                    .map(product => product.id === this.props.product
                                        && <img key={Math.random()} src={product.gallery[0]} alt={product.id} className="main-image"></img>))
                        }
                        {
                            this.props.products
                                .map(x => x
                                    .map(product => product.id === this.props.product
                                        && <div key={product.id} className="product-info">
                                            <h3>{product.brand}</h3>
                                            <p>{product.name}</p>
                                            <p>PRICE:</p>
                                            {product.prices.map(x => x.currency === this.props.selectedCurrency
                                                && <p key={Math.random()}>{currenciesSymbols[this.props.selectedCurrency] || '$'}{x.amount}</p>)}
                                            {this.props.products.map(x => x
                                                .map(product => product.id === this.props.product && product.attributes
                                                    .map(attribute => <div key={attribute.id}>
                                                        <p>{attribute.id}</p>
                                                        {attribute.items
                                                            .map(item => attribute.type === 'swatch'
                                                            ? <button key={item.id} style={{backgroundColor: item.value}}></button>
                                                            : <button key={item.id}>{item.value}</button> )}
                                                    </div>)))}
                                        </div>))
                        }
                    </div>
                </div>

            </main>
        );
    }
}
