import React from "react";
import Wrapper from "../Wrapper";
import { connect } from "react-redux";
import { addCartItem } from '../../redux/actions/cart';
import { selectCategory } from '../../redux/actions/categories';
import { requestProducts, requestProductById } from '../../redux/actions/products';
import ProductWrapper from "./ProductWrapper";


class ProductPage extends React.Component {
    constructor() {
        super();
        this.state = {
            product: undefined,
        }
    }

    selectAndLoadCategory() {
        const foundCategory = this.props.categories.find(x => x === this.props.category);
        if (foundCategory) {
            this.props.selectCategory(foundCategory);
            this.props.requestProductById(this.state.product.id)
        } else {
            window.location.href = '/';
        }
    }

    componentDidMount() {
        this.selectAndLoadCategory();
        console.log(this.props.product)
    }

    componentDidUpdate() {
        this.selectAndLoadCategory();
    }

    // static getDerivedStateFromProps(props, current_state) {
    //     if (current_state.product?.id !== props.product) {
    //         return {
    //             product: this.props.product
    //         }
    //     }
    //     return null
    // }

    render() {

        if (this.props.product) 
            return (
                <Wrapper>
                    <div className="wrapper">
                        <div className="side-images">
                            {
                                this.props.product.gallery.map(x => <div key={x}
                                    className="side-image"
                                    onClick={() => {
                                        this.setState({
                                            mainImage: x
                                        });
                                    }}>
                                    <img src={x} alt="" />
                                </div>)
                            }
                        </div>
                        <ProductWrapper product={this.props.product} />
                    </div>
                </Wrapper>
            );
    }
}

const mapStateToProps = state => {
    return {
        selectedCurrency: state.currencies.selected,
        product: state.product,
        categories: state.categories.list
    };
};

export default connect(
    mapStateToProps,
    { addCartItem, selectCategory, requestProductById }
)(ProductPage);
