import React from "react";
import { connect } from "react-redux";
import { addProductAttribute } from "../redux/actions/products"

class ToggleButtons extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        }
    }

    componentDidUpdate() {
        console.log(this.props.productAttributes)
    }

    render() {
        const setButtonClassName = (type, value, id) => {
            if (this.props[id] === value) {
                return type === "swatch" ? "attribute-color-active" : "attribute-text-active";
            } else {
                return "attribute-button";
            }
        }
        if (this.props.product) {
            return (
                this.props.product.attributes.map(attribute =>
                    <div className="attribute-buttons">
                        <h4>{attribute.id}</h4>
                        <div className="attributes-wrapper" key={attribute.id}>
                            {attribute.items
                                .map(item => <button
                                    onClick={() => {
                                        if (!this.props.productAttributes.find(obj => Object.keys(obj).includes(attribute.id))) {
                                            this.props.addProductAttribute({[attribute.id + product.id]: item.value});
                                        }
                                        console.log(this.props.productAttributes)
                                            //this.props.addProductAttribute({[attribute.id]: item.value});

                                        //) 

                                        // console.log(this.props.productAttributes)
                                    }}
                                    className={setButtonClassName(attribute.type, item.value, attribute.id)}
                                    key={item.id}
                                    style={{ ...attribute.type === "swatch" ? { backgroundColor: item.value } : "" }}
                                >
                                    {attribute.type === "swatch" ? "" : item.value}
                                </button>
                                )}
                        </div>
                    </div>

                )
            );   
        }
        return null;
    }
}

const mapStateToProps = state => {
    return { 
        products: state.products.list,
        productAttributes: state.products.attributes,
    };
};

export default connect(
    mapStateToProps,
    { addProductAttribute }
)(ToggleButtons);
