import React from "react";
import items from "../../shared/itemImages";
import gql from "graphql-tag";
import { client } from "../ClientApi";
import { changeCategory } from "../../actions/changeCategory";
import { store } from "../..";

export class ProductCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      names: [],
      gallery: [],
      category: "",
    };
  }

  componentDidMount() {
    client
      .query({
        query: gql`
          query GetProds {
            category {
              products {
                name
                id
                gallery
              }
            }
          }
        `,
      })
      .then((result) =>
        this.setState((prevState) => ({
          name: result.data.category.products.map((prod) => prod.name),
          gallery: result.data.category.products.map((prod) => prod.gallery),
        }))
      );
  }

  render() {
    return (
      <div
        key={Math.random() * 10}
        //   className={imgObject.outOfStock === true ? "card-inactive" : "card"}
      >
        {/* {imgObject.outOfStock === true ? (
            <p className="out-of-stock">Out of stock</p>
          ) : (
            ""
          )} */}
        {/* <img className="item-image" src={imgObject.image}></img> */}
        <button onClick={() => {
            const newCategory = window.location.pathname.substring(1, window.location.pathname.lastIndexOf("/"));
            store.dispatch(changeCategory(newCategory));
            console.log(store.newCategory)
          }}></button>
        <img
          className="buy-icon"
          src="./buy-icon.svg"
        ></img>
        <div className="item-info">
          <p>Apollo Running Short</p>
          <p className="price">$50.00</p>
        </div>
      </div>
    );
  }
}
