import React from "react";
import { currenciesSymbols } from '../constants';

export default class ProductPage extends React.Component {
    getProduct() {
        return this.props.products.map(x => x.map(product => product.id === this.props.product && product.attributes));
    }

    render() {
        return (
            <main>
                <div className="wrapper">
                    <div className="side-images">
                        {
                            this.props.products
                                .map(x => x
                                    .map(product => product.id === this.props.product
                                        && product.gallery.map(x => <img key={x} alt="" className="side-image" src={x}></img>)))
                        }
                    </div>
                    <div className="product-wrapper">
                        {
                            this.props.products
                                .map(x => x
                                    .map(product => product.id === this.props.product
                                        && <img src={product.gallery[0]} alt={product.id} className="main-image"></img>))
                        }
                        {
                            this.props.products
                                .map(x => x
                                    .map(product => product.id === this.props.product
                                        && <div className="product-info">
                                            <h3>{product.brand}</h3>
                                            <p>{product.name}</p>
                                            <p>PRICE:</p>
                                            {product.prices.map(x => x.currency === this.props.selectedCurrency && <p>{currenciesSymbols[this.props.selectedCurrency] || '$'}{x.amount}</p>)}
                                            {this.getProduct().map(x => x && console.log(x))}
                                            {this.props.products.map(x => x.map(product => product.id === this.props.product && product.attributes)).map(attribute => (attribute.type === "text") ? console.log('kk') : '')}
                                        </div>))
                        }
                    </div>
                </div>

            </main>
        );
    }
}
