import React from "react";
import { currenciesSymbols } from '../constants';
import { ToggleButtons } from "./ToggleButtons";

export default class ProductPage extends React.Component {
    constructor() {
        super();
        this.state = {
            mainImage: '',
            product: undefined
        }
    }

    chooseProduct() {
        console.log(this.props.product, this.state.product)
        if (this.props.product !== this.state.product?.id) {
            this.setState({
                product: this.props.products.find(x => x.id === this.props.product)
            });
        }
    }

    componentDidUpdate() {
        this.chooseProduct.call(this);
    }

    componentDidMount() {
        this.chooseProduct.call(this);
    }

    render() {

        const product = this.state.product;

        if (!product) {
            return null;
        }

        return (
            <main>
                {this.props.isCartOpen && <div className="overlay"></div>}
                <div className="wrapper">
                    <div className="side-images">
                        {
                            product.gallery.map(x => <img key={x} alt=""
                                className="side-image"
                                src={x}
                                onClick={() => {
                                    this.setState({
                                        mainImage: x
                                    });
                                }}></img>)
                        }
                    </div>
                    <div className="product-wrapper">
                        {

                            <div key={Math.random()} className="main-image-wrapper"><img src={this.state.mainImage.length > 0
                                ? this.state.mainImage
                                : product.gallery[0]}
                                alt={product.id}
                                className="main-image"></img>
                            </div>
                        }
                        {
                            <div key={product.id} className="product-info">
                                <h3>{product.brand}</h3>
                                <p>{product.name}</p>
                                <ToggleButtons product={product}></ToggleButtons>
                                <h4>Price:</h4>
                                {product.prices.map(x => x.currency === this.props.selectedCurrency
                                    && <p key={Math.random()} className="price">{currenciesSymbols[this.props.selectedCurrency] || '$'}
                                        {x.amount}</p>)}
                                <button className="add-to-cart-button">
                                    Add to cart
                                </button>
                                <p className="product-description" dangerouslySetInnerHTML={{ __html: product.description }}></p>
                            </div>
                        }
                    </div>
                </div>

            </main>
        );
    }
}
