import { LOCATION_CHANGE } from 'connected-react-router';
import { combineReducers } from 'redux';
import { MEN, WOMEN } from '../types';

function consumerReducer(state = window.location.pathname.substring(window.location.pathname.lastIndexOf("/") + 1), action) {
    if (action.type === LOCATION_CHANGE) {
        return {...state, consumer: action.payload};
    } else if (action.type === MEN) {
        return {...state, consumer: 'men'};
    } else if (action.type === WOMEN) {
        return {...state, consumer: 'women'};
    }

    return state;
}

export const rootReducer = combineReducers({
    consumer: consumerReducer,
})
