import { combineReducers } from 'redux';
import { TECH, CLOTHES } from '../types';

function categoryReducer(state = 'clothes', action) {
    if (action.type === TECH) {
        return state = 'tech' ;
    } else if (action.type === CLOTHES) {
        return state = 'clothes';
    } 

    return state;
}

export const rootReducer = combineReducers({
    category: categoryReducer,
})
