import React from "react";
import items from "../../shared/itemImages";
import { ApolloClient, InMemoryCache } from "@apollo/client";
import gql from 'graphql-tag';

const cache = new InMemoryCache();

const client = new ApolloClient({
  // Provide required constructor fields
  cache: cache,
  uri: 'http://localhost:4000/graphql',

  // Provide some optional constructor fields
  name: 'react-web-client',
  version: '1.3',
  queryDeduplication: false,
  defaultOptions: {
    watchQuery: {
      fetchPolicy: 'cache-and-network',
    },
  },
});

export class ProductCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      names: [],
      gallery: []
    };
  }

  componentDidMount() {
    client
      .query({
        query: gql`
          query GetProds {
            category {
              products {
                name
                id
              }
            }
          }
        `,
      })
      .then((result) =>
        this.setState((prevState) => ({
          name: result.data.category.products.map((prod) => (prod.name)),
          images: result.data.category.products.map((prod) => (prod.name)),
        }))
      );
  }

  render() {
    return items
      .find((x) => x.images)
      .images.map((imgObject) => (
        <div
          key={Math.random() * 10}
          className={imgObject.outOfStock === true ? "card-inactive" : "card"}
        >
          {imgObject.outOfStock === true ? (
            <p className="out-of-stock">Out of stock</p>
          ) : (
            ""
          )}
          {console.log(this.state.name)}
          <img className="item-image" src={imgObject.image}></img>
          <img className="buy-icon" src="./buy-icon.svg"></img>
          <div className="item-info">
            <p>Apollo Running Short</p>
            <p className="price">$50.00</p>
          </div>
        </div>
      ));
  }
}
