import React from "react";

export class ToggleButtons extends React.Component {
    render() {
        return (
            <div className="attribute-buttons">
                {this.props.products.map(x => x.map(product => product.attributes.map(attribute => attribute.items.map(item => attribute.type === 'swatch' && product.id === this.props.product
                       ? <button
                            className="attribute-button"
                            key={item.id}
                            style={{ backgroundColor: item.value }}></button>
                        : <button
                            className="attribute-button"
                            key={item.id}>{item.value}</button> 
                ))))}
            </div>
        )
    }
}
