import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
} from "@apollo/client";
import React from "react";

export class ProductNames extends React.Component {
  render() {
    const { loading, error, data } = useQuery(gql`
      {
        category {
          products {
            name
            inStock
          }
        }
      }
    `);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;

    return data.category.products.map(({ name, inStock }) => (
      <div key={name}>
        <p>
          {name}: {inStock.toString()}
        </p>
      </div>
    ));
  }
}
