import * as actionTypes from "../../constants/actionTypes";
import { client } from "../../utils/ClientApi";
import gql from "graphql-tag";

export const requestCategories = async categories => async dispatch => {
    dispatch({ type: actionTypes.GET_CATEGORIES_START });
    try {
        const result = await client.query({
            query: gql`
                query requestCategories {
                    categories 
                }
            `,
        });
        console.log(result)
        dispatch({ type: actionTypes.GET_CATEGORIES_SUCCESS, payload: result.data.categories });
    } catch (e) {
        dispatch({ type: actionTypes.GET_CATEGORIES_FAILED, payload: result.data.categories });
    }
};
