import * as actionTypes from "../../constants/actionTypes";

const initialState = {
    isCartOpen: false
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.TOGGLE_CART:
            return {
                ...state,
                isCartOpen: !state.isCartOpen,
            };
        default:
            return state;
    }
}

export default reducer;
