import React from "react";
import { currenciesSymbols } from '../constants';

export default class ProductPage extends React.Component {
    render() {
        return (
            <main>
                <div className="wrapper">
                    <div className="side-images">
                        {
                            this.props.products.map(x => x
                                .map(product => product.id === this.props.product
                                    && product.gallery.map(x => <img key={x} alt="" className="side-image" src={x}></img>)))
                        }
                    </div>
                    <div className="product-wrapper">
                        {
                            this.props.products.map(x => x.map(product => product.id === this.props.product
                                && <img key={Math.random()} src={product.gallery[0]} alt={product.id} className="main-image"></img>))
                        }
                        {
                            this.props.products.map(x => x
                                .map(product => product.id === this.props.product
                                    && <div key={product.id} className="product-info">
                                        <h3>{product.brand}</h3>
                                        <p>{product.name}</p>
                                        {this.props.products.map(x => x.map(product => product.id === this.props.product && product.attributes
                                            .map(attribute => <div className="attributes-wrapper" key={attribute.id}>
                                                <h4>{attribute.id}</h4>
                                                {attribute.items
                                                    .map(item => attribute.type === 'swatch'
                                                        ? <button
                                                            className="attribute-button"
                                                            key={item.id}
                                                            style={{ backgroundColor: item.value }}></button>
                                                        : <button
                                                            className="attribute-button"
                                                            key={item.id}>{item.value}</button>)}
                                            </div>)))}
                                        <h4>Price:</h4>
                                        {product.prices.map(x => x.currency === this.props.selectedCurrency
                                            && <p key={Math.random()} className="price">{currenciesSymbols[this.props.selectedCurrency] || '$'}
                                                {x.amount}</p>)}
                                        <button className="add-to-cart-button">
                                            Add to cart
                                        </button>
                                        <p className="product-description">product.description</p>
                                    </div>))
                        }
                    </div>
                </div>

            </main>
        );
    }
}
