import React from "react";
import { currenciesSymbols } from '../../constants';
import ToggleButtons from "./ToggleButtons";
import { connect } from "react-redux";
import { addCartItem } from '../../redux/actions/cart';
import { selectCategory } from '../../redux/actions/categories';
import { requestProducts } from '../../redux/actions/products';
import { selectCurrency } from "../../redux/actions/currencies";

export class ProductWrapper extends React.Component {
    constructor() {
        super();
        this.state = {
            attributes: {}
        }
    }
    setAttributes(value) {
        this.setState({
            attributes: value
        });
    }


    isAllSelected(product, attributes) {
        for (let attr of product.attributes) {
            if (attributes[attr.id] === undefined) {
                return false;
            }
        }
        return true;
    }

    render() {

        const { product, mainImage, setAttributes } = this.props;

        return <div className="product-wrapper">
            <div className="main-image-wrapper">
                {product.inStock ? "" : <p className="out-of-stock">Out of stock</p>}
                <img src={mainImage.length > 0
                    ? mainImage
                    : product.gallery[0]}
                    alt={product.id}
                    className="main-image"></img>
            </div>
            <div key={product.id} className="product-info">
                <h3>{product.brand}</h3>
                <p>{product.name}</p>
                <ToggleButtons
                    product={product}
                    attributes={this.state.attributes}
                    setAttributes={this.setAttributes.bind(this)}
                />
                <h4>Price:</h4>
                <p key={Math.random()} className="price">
                {currenciesSymbols[this.props.selectedCurrency] || '$'} {product.prices.find(x => x.currency === this.props.selectedCurrency)?.amount} {console.log(product.prices, selectCurrency)}</p>
                <button
                    onClick={() => this.isAllSelected() && this.props.addCartItem({ ...product, attrValues: this.state.attributes })}
                    className={'add-to-cart-button ' + ((this.isAllSelected(product, this.state.attributes)) && product.inStock ? '' : 'add-to-cart-button-disabled')}>
                    Add to cart
            </button>
                <div className="product-description" dangerouslySetInnerHTML={{ __html: product.description }}></div>
            </div>
        </div>
    }
}

const mapStateToProps = state => {
    return {
        selectedCurrency: state.currencies.selected,
        products: state.products.list,
        categories: state.categories.list
    };
};

export default connect(
    mapStateToProps,
    { addCartItem, selectCategory, requestProducts }
)(ProductWrapper);
