import * as actionTypes from "../../constants/actionTypes";
import { client } from "../../utils/ClientApi";
import gql from "graphql-tag";

export const requestPrices = () => async dispatch => {
    dispatch({ type: actionTypes.GET_PRODUCTS_START });
    try {
        const result = await client.query({
            query: gql`
                query getPrices {
                    categories {
                        products {
                            prices {
                              amount
                            }
                        }
                    }
                }
            `
        });
        result.data.categories.map((x) => x.products
        .map(product => product.prices.map(y => console.log(y))));
        //console.log(allPrices)

    dispatch({ type: actionTypes.GET_PRICES_SUCCESS, payload: result})
    } catch (e) {
        dispatch({ type: actionTypes.GET_PRICES_FAILED })
    }
}
