import React from "react";
import { currenciesSymbols } from '../../constants';
import ToggleButtons from "./ToggleButtons";
import { connect } from "react-redux";
import { addCartItem } from '../../redux/actions/cart';
import { selectCategory } from '../../redux/actions/categories';
import { requestProducts } from '../../redux/actions/products';


export class ProductWrapper extends React.Component {
    render() {
        return <div className="product-wrapper">
            <div className="main-image-wrapper">
                {this.state.product.inStock ? "" : <p className="out-of-stock">Out of stock</p>}
                <img src={this.state.mainImage.length > 0
                    ? this.state.mainImage
                    : this.state.product.gallery[0]}
                    alt={this.state.product.id}
                    className="main-image"></img>
            </div>
            <div key={this.state.product.id} className="product-info">
                <h3>{this.state.product.brand}</h3>
                <p>{this.state.product.name}</p>
                <ToggleButtons
                    product={this.state.product}
                    attributes={this.state.attributes}
                    setAttributes={this.setAttributes.bind(this)}
                />
                <h4>Price:</h4>
                <p key={Math.random()} className="price">
                    {currenciesSymbols[this.props.selectedCurrency] || '$'} {this.state.product.prices.find(x => x.currency === this.props.selectedCurrency)?.amount}</p>
                <button
                    onClick={() => this.isAllSelected() && this.props.addCartItem({ ...this.state.product, attrValues: this.state.attributes })}
                    className={'add-to-cart-button ' + (this.isAllSelected() && this.state.product.inStock ? '' : 'add-to-cart-button-disabled')}>
                    Add to cart
            </button>
                <div className="product-description" dangerouslySetInnerHTML={{ __html: this.state.product.description }}></div>
            </div>
        </div>
    }
}


const mapStateToProps = state => {
    return {
        selectedCurrency: state.currencies.selected,
        products: state.products.list,
        categories: state.categories.list
    };
};

export default connect(
    mapStateToProps,
    { addCartItem, selectCategory, requestProducts }
)(ProductWrapper);
