import React from "react";
import Wrapper from "../Wrapper";
import { connect } from "react-redux";
import { addCartItem } from "../../redux/actions/cart";
import ToggleButtons from "../product/ToggleButtons";
import { currenciesSymbols } from "../../constants";

class CartPage extends React.Component {
    render() {
        return (
            <Wrapper>
                <p className="cart-title">cart</p>

                {
                    this.props.cartItems.map(item =>
                        <div className="cart-item-container">
                            <div key={item.id} className="product-info">
                                <h3>{item.brand}</h3>
                                <p>{item.name}</p>
                                <ToggleButtons
                                    product={item}
                                    attributes={this.state.attributes}
                                    setAttributes={this.setAttributes.bind(this)}
                                />
                                <h4>Price:</h4>
                                <p key={Math.random()} className="price">
                                    {currenciesSymbols[this.props.selectedCurrency] || '$'} {item.prices.find(x => x.currency === this.props.selectedCurrency)?.amount}</p>
                            </div>
                        </div>
                    )
                }
                {console.log(this.props.cartItems)}
            </Wrapper>
        )
    }
}

const mapStateToProps = state => {
    return {
        selectedCurrency: state.currencies.selected,
        cartItems: state.cart.list,
        products: state.products.list,
    };
};

export default connect(
    mapStateToProps,
    { addCartItem }
)(CartPage);
