import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
  useMutation,
} from "@apollo/client";
import React from "react";

export function ProductNames() {
    const { loading, error, data } = useQuery(gql`
      {
        category {
          products {
            name
            inStock
            category
            gallery
          }
        }
      }
    `);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;

    return data.category.map(({ products }) => (
      <div key={products}>
        <p>
        {
            category === 'clothes' ? `${products}: ${category}` : ""
        }
        {
          category === 'clothes' ? console.log(products) : ''
        }
        </p>
      </div>
    ));
}
