import React from "react";

export default class ProductPage extends React.Component {

    constructor(props) {
        super(props);
    }

    getImages() {
        const images = this.props.products
        .map(x => x
            .map(product => product.id === this.props.product));
                return images;
    }

    render() {
        return (
            <main>
                <div className="side-images">
                    {

                                this.getImages() && this.images.gallery.map(x => <img alt="" className="side-image" src={x}></img>)
                    }
                </div>
                <div className="product-wrapper">
                    {
                        this.props.products
                            .map(x => x
                                .map(product => product.id === this.props.product
                                    && <img src={product.gallery[0]}></img>))
                    }
                </div>
            </main>
        );
    }
}
