import React from "react";

export class ToggleButtons extends React.Component {
    render() {
        return (
                this.props.products.map(x => x
                    .map(product => product.id === this.props.product
                        && 
                            this.props.products.map(x => x.map(product => product.id === this.props.product && product.attributes
                                .map(attribute => <div className="attributes-wrapper" key={attribute.id}>
                                    <h4>{attribute.id}</h4>
                                    <ToggleButtons products={this.props.products}></ToggleButtons>
                                    <div className="attribute-buttons">
                                        {attribute.items
                                            .map(item => attribute.type === 'swatch'
                                                ? <button
                                                    className="attribute-button"
                                                    key={item.id}
                                                    style={{ backgroundColor: item.value }}></button>
                                                : <button
                                                    className="attribute-button"
                                                    key={item.id}>{item.value}</button>
                                            )}
                                    </div>
                                    </div>
        ))))))
    }
}
