import React from "react";

export class Cart extends React.Component {
    constructor(props) {
        super(props);

    }

    render() {

        return (
            <div className="cart-dd-wrapper">
                <img alt="" className={this.state.isCartOpen ? "cart open" : "cart"} src="../../empty-cart.svg" onClick={() => {
                            this.setState({
                                isCartOpen: true
                            })
                }}></img>
                {(this.state.isCartOpen) && <div role="list" className="cart-list">
                    {
                        <button
                            type="button"
                            className="dd-list-item"
                        >
                        </button>
                    }
                    </div>
                    }

            </div>
        )
    }
}
