import React from "react";

export class Cart extends React.Component {
    constructor(props) {
        super(props);
    }

    toggleCart = () => {
        this.props.isCartOpen = !this.props.isCartOpen;
        console.log(this.props.isCartOpen)
    };

    render() {

        return (
            <div className="cart-dd-wrapper">
                <img alt="" className={this.props.isCartOpen ? "cart open" : "cart"} src="../../empty-cart.svg" onClick={this.toggleCart}></img>
                {(this.props.isCartOpen) && <div role="list" className="cart-list">
                    {
                        <button
                            type="button"
                            className="dd-list-item"
                        >
                        </button>
                    }
                    </div>
                    }

            </div>
        )
    }
}
