import * as actionTypes from "../../constants/actionTypes";

const initialState = {
    isCartOpen: false
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.OPEN_CART:
            return {
                ...state,
                isCartOpen: true,
            };
        case actionTypes.CLOSE_CART:
            return {
                ...state,
                isCartOpen: false,
            };
        default:
            return state;
        } 
    }

export default reducer;
