import React from "react";
import { connect } from "react-redux";


export class Cart extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isCartOpen: false
        }
    }

    toggleCart = () => {
        this.setState((prevState) => ({
            isCartOpen: !prevState.isCartOpen,
        }));
        console.log(this.props.isCartOpen)
    };

    render() {

        return (
            <div className="cart-dd-wrapper">
                <img alt="" className={this.state.isCartOpen ? "cart-open" : "cart"} src="../../empty-cart.svg" onClick={this.toggleCart}></img>
                {(this.state.isCartOpen) && <div role="list" className="cart-list">

                    </div>
                    }

            </div>
        )
    }
}

const mapStateToProps = state => {
    return { ...state.isCartOpen };
};

export default connect(
    mapStateToProps
)(Cart);
