import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
  useMutation,
  graphql
} from "@apollo/client";
import React from "react";

const { loading, error, data } = graphql(gql`
      {
        category {
          products {
            name
            inStock
            category
          }
        }
      }
    `);

export class ProductNames extends React.Component {
    render() {
    return data.category.products.map(({ name, category }) => (
      <div key={name}>
        <p>
        {
            category === 'tech' ? `${name}: ${category}` : ''
        }
        </p>
      </div>
    ));
    }  
}
