import React from "react";

export class ToggleButtons extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedTextOption: '',
            selectedColor: '',
        }
    }

    render() {
        return (
            this.props.products.map(x => x
                .map(product => product.id === this.props.product
                    && product.attributes
                        .map(attribute =>
                            <div className="attribute-buttons">
                                <h4>{attribute.id}</h4>
                                <div className="attributes-wrapper" key={attribute.id}>
                                    {attribute.items
                                        .map(item => attribute.type === "swatch"
                                            ? <button 
                                                onClick={() => {
                                                    this.setState({
                                                        selectedColor: item.value
                                                    });
                                                    console.log(this.state.selectedColor)
                                                }}
                                                className="attribute-button"
                                                key={item.id}
                                                style={{ backgroundColor: item.value }}></button>
                                            : <button
                                                onClick={() => {
                                                    this.setState({
                                                        selectedTextOption: item.value
                                                    });
                                                    console.log(this.state.selectedTextOption);
                                                }}
                                                className={this.state.selectedTextOption === item.value 
                                                ? "attribute-button-active"
                                                : "attribute-button"}
                                                key={item.id}>{item.value}</button>
                                        )}
                                </div>
                            </div>

                        ))))
    }
}
