import { CHANGE_CATEGORY } from "../../types";

const initialState = {
  name: "",
  products: []
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case CHANGE_CATEGORY:
        const { category } = action.payload;
        return {
            ...state
        }
  }
}

export default reducer;
